import java.util.*;

/**
 * https://leetcode.com/problems/minimum-window-substring/
 * P076 Minimum Window Substring
 * Hard
 * <p>
 * Given a string S and a string T, find the minimum window in S which will contain all the characters in T in complexity O(n).
 * <p>
 * Note:
 * If there is no such window in S that covers all characters in T, return the empty string "".
 * If there is such window, you are guaranteed that there will always be only one unique minimum window in S.
 */

// TODO optimize algorithm to beat time limit

public class LC076_Minimum_Window_Substring {

    /**
     * Version C
     * Even faster by only starting indexes at the char that is in the target
     * Still failed max time limit
     */
    public String minWindow(String s, String t) {

        int L = s.length();
        int startLength = t.length();

        // set up t_set and hashmap for counting all in once
        Set<Character> t_set = new HashSet<>();
        Map<Character, Integer> hmp = new HashMap<>();
        for (char c : t.toCharArray()) {
            t_set.add(c);
            if (!hmp.containsKey(c)) {
                hmp.put(c, 1);
            } else {
                hmp.replace(c, hmp.get(c) + 1);
            }
        }

        List<Integer> indexes_list = new ArrayList<>(Arrays.asList());
        for (int i = 0; i < L; i++) {
            if (t_set.contains(s.charAt(i))) {
                indexes_list.add(i);
            }
        }

        for (int lenth = startLength; lenth <= L; lenth += 1) {
            for (int i : indexes_list) {
                String window = s.substring(i, Math.min(i + lenth, s.length())); // 防止切片长度溢出
                boolean flag = false;
                for (Character c : t_set) {
                    int count = 0;
                    for (char cc : window.toCharArray()) {
                        if (cc == c) {
                            count += 1;
                        }
                    }
                    if (count < hmp.get(c)) {
                        flag = true;
                        break;
                    }
                }
                if (!flag) {
                    return window;
                }
            }
        }
        return "";
    }

    public static void main(String[] args) {
        LC076_Minimum_Window_Substring testCase = new LC076_Minimum_Window_Substring();
        assert testCase.minWindow("ABCDE", "Z").equals("") : "Edge 1";
        assert testCase.minWindow("A", "A").equals("A") : "Edge 2";
        assert testCase.minWindow("a", "aa").equals("") : "Edge 3";
        assert testCase.minWindow("ADOBECODEBANC", "ABC").equals("BANC") : "Example 1";

        String long_str = "";
        String long_target = "";

        assert testCase.minWindow(long_str, long_target).equals("") : "Long case";

        System.out.println("All passed");

    }

}

